CREATE TABLE `bodyinfo`  (
  `id` int(5) NOT NULL AUTO_INCREMENT,
  `uid` int(5) NULL DEFAULT NULL COMMENT '用户id',
  `weight` int(2) NULL DEFAULT NULL COMMENT '体重 0-40以下 1-40-50kg 2-50-60kg 3-60-70kg 4-70-80kg 5-80kg以上',
  `cold` varchar(30) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '身体冰凉',
  `bodyAttent` varchar(30) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '身体状况关注',
  `chronic` varchar(30) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '慢性疾病',
  `urine` varchar(30) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '小便情况',
  `shit` varchar(30) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '大便情况',
  `symptom` varchar(30) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '近期状况',
  `tightness` int(2) NULL DEFAULT NULL COMMENT '胸闷 1没有  2有  3经常',
  `medHistory` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '病史',
  PRIMARY KEY (`id`) USING BTREE
) ENGINE = InnoDB AUTO_INCREMENT = 9 CHARACTER SET = utf8 COLLATE = utf8_general_ci ROW_FORMAT = Compact;

