DROP TABLE IF EXISTS `salary`;
CREATE TABLE `salary`  (
  `id` int(11) NOT NULL AUTO_INCREMENT COMMENT '工资id',
  `uid` int(11) NULL DEFAULT NULL COMMENT '关联用户id',
  `performance` double(255, 1) NULL DEFAULT NULL COMMENT '奖励',
  `factSalary` double(255, 1) NULL DEFAULT NULL COMMENT '实发工资',
  `deductSituation` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '扣减情况',
  `deductSalary` double(10, 1) NOT NULL COMMENT '扣减工资',
  `mid` int(11) NULL DEFAULT NULL COMMENT '月份id',
  PRIMARY KEY (`id`) USING BTREE
) ENGINE = InnoDB AUTO_INCREMENT = 3 CHARACTER SET = utf8 COLLATE = utf8_general_ci ROW_FORMAT = Compact;

