-- ----------------------------
-- View structure for sss
-- ----------------------------
DROP VIEW IF EXISTS `sss`;
CREATE ALGORITHM = UNDEFINED DEFINER = `root`@`localhost` SQL SECURITY DEFINER VIEW `sss` AS select `cost`.`id` AS `id`,`cost`.`bid` AS `bid`,`cost`.`uid` AS `uid`,`cost`.`costDate` AS `costDate`,`cost`.`eaxmCost` AS `eaxmCost`,`cost`.`foodCost` AS `foodCost`,`cost`.`lineCost` AS `lineCost`,`cost`.`orderStatus` AS `orderStatus`,`bed`.`price` AS `price`,`bed`.`status` AS `status`,`user`.`userName` AS `userName` from ((`cost` join `bed`) join `user`) where ((`cost`.`bid` = `bed`.`id`) and (`cost`.`uid` = `user`.`id`));

SET FOREIGN_KEY_CHECKS = 1;
